import React, { useState, useEffect } from 'react';

function MonopolyTimer() {
  const [startTime, setStartTime] = useState(null);
  const [elapsedTime, setElapsedTime] = useState(0);
  const [history, setHistory] = useState([{ time: '00:00:00', message: 'Welcome to Monopoly' }]);

  useEffect(() => {
    // Start the timer when the component mounts
    setStartTime(Date.now());

    const interval = setInterval(() => {
      // Calculate elapsed time every second
      if (startTime) {
        setElapsedTime(Date.now() - startTime);
      }
    }, 1000);

    // Clean up the interval when the component unmounts
    return () => clearInterval(interval);
  }, [startTime]);

  const formatTime = (milliseconds) => {
    const totalSeconds = Math.floor(milliseconds / 1000);
    const hours = Math.floor(totalSeconds / 3600);
    const minutes = Math.floor((totalSeconds % 3600) / 60);
    const seconds = totalSeconds % 60;

    return `${String(hours).padStart(2, '0')}:${String(minutes).padStart(2, '0')}:${String(seconds).padStart(2, '0')}`;
  };

  const addHistoryEntry = (message) => {
    setHistory([...history, { time: formatTime(elapsedTime), message }]);
  };

  // Example: Simulating earning a property (you would replace this with your game logic)
  const handleEarnProperty = (propertyName) => {
    addHistoryEntry(`Earned ${propertyName}`);
  };

  return (
    <div style={{
      backgroundColor: '#98FB98',
      width: '300px',
      padding: '20px',
      borderRadius: '10px',
      fontFamily: 'Arial, sans-serif'
    }}>
      <div style={{
        backgroundColor: '#D3D3D3',
        padding: '10px',
        borderRadius: '5px',
        marginBottom: '10px',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
      }}>
        <span style={{ fontWeight: 'bold', fontSize: '1.2em' }}>BANK</span>
        <span>â–¼</span>
      </div>

      <div style={{
        backgroundColor: '#008080',
        padding: '15px',
        borderRadius: '5px',
        color: 'white'
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '10px' }}>
          <span style={{ fontWeight: 'bold' }}>Time Tracker</span>
          <span>{formatTime(elapsedTime)}</span>
        </div>

        {history.map((entry, index) => (
          <div key={index} style={{ borderBottom: index < history.length - 1 ? '1px solid white' : 'none', paddingBottom: '5px', paddingTop: '5px' }}>
            <span>{entry.time}</span> <span style={{ marginLeft: '10px' }}>{entry.message}</span>
          </div>
        ))}

        <div style={{ marginTop: '10px', display: 'flex', justifyContent: 'flex-end' }}>
          <div style={{ height: '100px', width: '10px', backgroundColor: '#ADD8E6', position: 'relative' }}>
            <div style={{ position: 'absolute', top: '0', left: '0', width: '100%', height: '50%', backgroundColor: '#00CED1' }}></div>
            <div style={{ position: 'absolute', bottom: '0', left: '0', width: '100%', height: '50%', backgroundColor: '#ADD8E6' }}></div>
          </div>
        </div>


      </div>
    </div>
  );
}

export default MonopolyTimer;
