import React from "react";
import styled from "styled-components";

const BoardContainer = styled.div`
  width: 600px;
  height: 600px;
  background-color: #ddd;
  display: grid;
  grid-template-columns: repeat(11, 1fr);
  grid-template-rows: repeat(11, 1fr);
  border: 2px solid black;
`;

const Cell = styled.div`
  border: 1px solid #999;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 0.7em;
  text-align: center;
  background-color: ${(props) => props.color || "white"};
`;

const CornerCell = styled(Cell)`
  font-weight: bold;
  background-color: #f0f0f0;
`;

const PropertyCell = styled(Cell)`
  background-color: ${(props) => props.color || "lightblue"};
`;

const MonopolyBoard = () => {
  const boardLayout = [
    "GO",
    "Mediterranean Avenue",
    "Community Chest",
    "Baltic Avenue",
    "Income Tax",
    "Reading Railroad",
    "Oriental Avenue",
    "Chance",
    "Vermont Avenue",
    "Connecticut Avenue",
    "Jail",
    "St. Charles Place",
    "Electric Company",
    "States Avenue",
    "Virginia Avenue",
    "Pennsylvania Railroad",
    "St. James Place",
    "Community Chest",
    "Tennessee Avenue",
    "New York Avenue",
    "Free Parking",
    "Kentucky Avenue",
    "Chance",
    "Indiana Avenue",
    "Illinois Avenue",
    "B&O Railroad",
    "Atlantic Avenue",
    "Ventnor Avenue",
    "Water Works",
    "Marvin Gardens",
    "Go To Jail",
    "Pacific Avenue",
    "North Carolina Avenue",
    "Community Chest",
    "Pennsylvania Avenue",
    "Short Line",
    "Chance",
    "Park Place",
    "Luxury Tax",
    "Boardwalk",
  ];

  const getCellStyle = (property) => {
    switch (property) {
      case "GO":
      case "Jail":
      case "Free Parking":
      case "Go To Jail":
        return { type: "corner" };
      case "Mediterranean Avenue":
        return { type: "property", color: "#A3502B" };
      case "Baltic Avenue":
        return { type: "property", color: "#A3502B" };
      case "Oriental Avenue":
        return { type: "property", color: "#79CBE3" };
      case "Vermont Avenue":
        return { type: "property", color: "#79CBE3" };
      case "Connecticut Avenue":
        return { type: "property", color: "#79CBE3" };
      case "St. Charles Place":
        return { type: "property", color: "#E84C9B" };
      case "States Avenue":
        return { type: "property", color: "#E84C9B" };
      case "Virginia Avenue":
        return { type: "property", color: "#E84C9B" };
      case "St. James Place":
        return { type: "property", color: "#F37328" };
      case "Tennessee Avenue":
        return { type: "property", color: "#F37328" };
      case "New York Avenue":
        return { type: "property", color: "#F37328" };
      case "Kentucky Avenue":
        return { type: "property", color: "#E9272A" };
      case "Indiana Avenue":
        return { type: "property", color: "#E9272A" };
      case "Illinois Avenue":
        return { type: "property", color: "#E9272A" };
      case "Atlantic Avenue":
        return { type: "property", color: "#FDE724" };
      case "Ventnor Avenue":
        return { type: "property", color: "#FDE724" };
      case "Marvin Gardens":
        return { type: "property", color: "#FDE724" };
      case "Pacific Avenue":
        return { type: "property", color: "#23B14D" };
      case "North Carolina Avenue":
        return { type: "property", color: "#23B14D" };
      case "Pennsylvania Avenue":
        return { type: "property", color: "#23B14D" };
      case "Park Place":
        return { type: "property", color: "#024873" };
      case "Boardwalk":
        return { type: "property", color: "#024873" };
      case "Reading Railroad":
      case "Pennsylvania Railroad":
      case "B&O Railroad":
      case "Short Line":
        return { type: "railroad" };
      case "Electric Company":
      case "Water Works":
        return { type: "utility" };
      case "Community Chest":
      case "Chance":
        return { type: "chance" };
      case "Income Tax":
      case "Luxury Tax":
        return { type: "tax" };
      default:
        return {};
    }
  };

  const renderBoard = () => {
    return boardLayout.map((property, index) => {
      const cellStyle = getCellStyle(property);
      let cellContent;

      switch (cellStyle.type) {
        case "corner":
          cellContent = React.createElement(
            CornerCell,
            { key: index },
            property
          );
          break;
        case "property":
          cellContent = React.createElement(
            PropertyCell,
            { key: index, color: cellStyle.color },
            property
          );
          break;
        default:
          cellContent = React.createElement(Cell, { key: index }, property);
      }

      return cellContent;
    });
  };

  return React.createElement(BoardContainer, null, renderBoard());
};

export default MonopolyBoard;
