import React from "react";
import styled from "styled-components";

const PlayerContainer = styled.div`
  border: 1px solid #ccc;
  padding: 10px;
  margin-bottom: 10px;
  background-color: ${(props) => (props.isCurrent ? "#ccffcc" : "white")};
`;

const PlayerPanel = ({ player, money, currentPlayer }) => {
  return React.createElement(
    PlayerContainer,
    { isCurrent: player === currentPlayer },
    React.createElement("h3", null, `Player ${player}`),
    React.createElement("p", null, `Money: $${money}`)
  );
};

export default PlayerPanel;
