import React, { useState, useEffect } from "react";
import styled from "styled-components";
import MonopolyBoard from "./components/MonopolyBoard.js";
import PlayerPanel from "./components/PlayerPanel.js";
import BankPanel from "./components/BankPanel.js";

const AppContainer = styled.div`
  display: flex;
  height: 100vh;
  font-family: sans-serif;
`;

const BoardContainer = styled.div`
  flex: 2;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const SidebarContainer = styled.div`
  flex: 1;
  background-color: #eee;
  padding: 20px;
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const App = () => {
  const [player1Money, setPlayer1Money] = useState(1500);
  const [player2Money, setPlayer2Money] = useState(1500);
  const [time, setTime] = useState(0);
  const [currentPlayer, setCurrentPlayer] = useState(1); // 1 or 2

  useEffect(() => {
    const timer = setInterval(() => {
      setTime((prevTime) => prevTime + 1);
    }, 1000); // Update every second

    return () => clearInterval(timer); // Cleanup on unmount
  }, []);

  const handleTransaction = (player, amount) => {
    if (player === 1) {
      setPlayer1Money((prevMoney) => prevMoney + amount);
    } else {
      setPlayer2Money((prevMoney) => prevMoney + amount);
    }
  };

  const switchPlayer = () => {
    setCurrentPlayer((prevPlayer) => (prevPlayer === 1 ? 2 : 1));
  };

  return (
    <AppContainer>
      <BoardContainer>
        <MonopolyBoard />
      </BoardContainer>
      <SidebarContainer>
        <h2>Monopoly Game</h2>
        <p>Time: {time} seconds</p>
        <PlayerPanel
          player={1}
          money={player1Money}
          currentPlayer={currentPlayer}
        />
        <PlayerPanel
          player={2}
          money={player2Money}
          currentPlayer={currentPlayer}
        />
        <BankPanel onTransaction={handleTransaction} />
        <button onClick={switchPlayer}>Switch Player</button>
      </SidebarContainer>
    </AppContainer>
  );
};

export default App;
